require 5.004;
use strict;

$::VERSION = '2.02';

sub config_lib {
  system("cd urilib ; ./configure --with-pic ; make all")
}

use ExtUtils::MakeMaker;
WriteMakefile(
	'VERSION' => $::VERSION,
	'CONFIGURE' => \&config_lib,
	'NAME'	 => 'URIC',
	'LIBS'   => ["-Lurilib -luri"],
	'INC'	 => '-Iurilib',
	'OBJECT' => 'URIC_wrap.o'
);

package MY;

sub postamble
{
  return <<EOF
clean realclean test ::
	cd urilib ; \$(MAKE) \$@

pack : clean realclean
	cd .. ; gtar -zchvf uri-$::VERSION.tar.gz uri-$::VERSION
EOF
}
